mapper.select:
SELECT

FROM sys_role a

WHERE a.id = #{id}

mapper.update:
UPDATE sys_role SET
role_name = #{roleName},
role_flag = #{roleFlag},
data_scope = #{dataScope},
status = #{status}
WHERE id = #{id}

mapper.insert:
INSERT INTO sys_menu_role(
    menu_id,
    role_id
) VALUES (
    #{menuId},
    #{roleId}
)
